// /routes/CalendarPage.jsx
import React, { useRef, useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import FloatingButtons from "../components/FloatingButtons.jsx";
import timeGridPlugin from "@fullcalendar/timegrid";
import "../styles/calendar.css"; // 기존 감성 스타일 CSS 연결

function CalendarPage() {
  const calendarRef = useRef(null);
  const [currentMonthYear, setCurrentMonthYear] = useState("");
  const [view, setView] = useState("dayGridMonth");
  const updateMonthYear = () => {   // 현재 달 업데이트
    const api = calendarRef.current?.getApi();
    if (api) {
      setCurrentMonthYear(api.view.title); 
    }
  };

  useEffect(() => {
    const calendarApi = calendarRef.current?.getApi();
    if (calendarApi) {
      setCurrentMonthYear(calendarApi.view.title);
    }
  }, []);

  const handleToday = () => {
    const api = calendarRef.current?.getApi();
    api?.today();
    updateMonthYear();
  };

  const handlePrev = () => {
    const api = calendarRef.current?.getApi();
    api?.prev();
    updateMonthYear();
  };

  const handleNext = () => {
    const api = calendarRef.current?.getApi();
    api?.next();
    updateMonthYear();
  };

  const changeView = (viewName) => {
    const api = calendarRef.current?.getApi();
    api?.changeView(viewName);
    updateMonthYear();
  };

  return (
    <div className="calendar-page">
      {/* 커스텀 헤더 */}
      <header className="calendar-header">
        <div className="calender-section calendar-left">
        <button className="fc-button" onClick={handlePrev}>‹</button>
        <button className="fc-button" onClick={handleNext}>›</button>
        <button 
          className={`fc-button gray ${view ==="today" ? "active" : ""}`}
          onClick={handleToday}>today</button>
        </div>

        <div className="calender-section calendar-center">
          <span className="calendar-date">{currentMonthYear}</span>
        </div>

        <div className="calender-section calendar-right">
        <button 
          className={`fc-button ${view === "timeGridDay" ? "active" : ""}`} 
          onClick={() => changeView("timeGridDay")}>day</button>
        <button 
          className={`fc-button ${view === "timeGridWeek" ? "active" : ""}`} 
          onClick={() => changeView("timeGridWeek")}>week</button>
        <button 
          className={`fc-button ${view === "dayGridMonth" ? "active" : ""}`} 
          onClick={() => changeView("dayGridMonth")}>month</button>
      </div>
    </header>

      {/* 캘린더 */}
      <div className="calendar-container">
        <FullCalendar
          ref={calendarRef}
          plugins={[dayGridPlugin, timeGridPlugin]}
          initialView="dayGridMonth"
          headerToolbar={{
            start: "prev,next today",
            center: "title",
            end: "dayGridMonth,timeGridDay,timeGridWeek"
          }}
          events={[]}
          height="auto"
          dayHeaderContent={(args) => {
            const weekday = args.date.toLocaleDateString("en-US", { weekday: "short" });
            return <span>{weekday}</span>;
          }}
        />
      </div>

      {/* 플로팅 버튼 (오른쪽 하단) */}
      <FloatingButtons />
    </div>
  );
}

export default CalendarPage;
