// /routes/CalendarPage.jsx
import React from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import "../styles/calendar.css"; // 기존 감성 스타일 CSS 연결
import FloatingButtons from "../components/FloatingButtons.jsx";
import timeGridPlugin from "@fullcalendar/timegrid";
import { useRef, useState, useEffect } from "react";

function CalendarPage() {
  const [currentMonthYear, setCurrentMonthYear] = useState("");
  useEffect(() => {
    const calendarApi = calendarRef.current?.getApi();
    if (calendarApi) {
      setCurrentMonthYear(calendarApi.view.title);
    }
  }, []);
  const calendarRef = useRef(null);
  const handleToday = () => {
    const api = calendarRef.current?.getApi();
    api?.today();
  };

  const handlePrev = () => {
    const api = calendarRef.current?.getApi();
    api?.prev();
  };

  const handleNext = () => {
    const api = calendarRef.current?.getApi();
    api?.next();
  };

  const changeView = (viewName) => {
    const api = calendarRef.current?.getApi();
    api?.changeView(viewName);
  };

  return (
    <div className="calendar-page">
      {/* 커스텀 헤더 */}
      <header className="calendar-header">
        <div className="calendar-left">shedAI</div>

        <div className="calendar-center">
          <button className="nav-btn" onClick={handlePrev}>‹</button>
          <span className="month-year">April 2025</span>
          <button className="nav-btn" onClick={handleNext}>›</button>
        </div>

        <div className="calendar-right">
        <button className="view-btn gray" onClick={handleToday}>today</button>
        <button className="view-btn" onClick={() => changeView("timeGridDay")}>day</button>
        <button className="view-btn" onClick={() => changeView("timeGridWeek")}>week</button>
        <button className="view-btn active" onClick={() => changeView("dayGridMonth")}>month</button>
      </div>
    </header>

      {/* 캘린더 */}
      <div className="calendar-container">
        <FullCalendar
          ref={calendarRef}
          plugins={[dayGridPlugin, timeGridPlugin]}
          initialView="dayGridMonth"
          headerToolbar={false}
          events={[]}
          height="auto"
          dayHeaderContent={(args) => {
            const weekday = args.date.toLocaleDateString("en-US", { weekday: "short" });
            return <span>{weekday}</span>;
          }}
        />
      </div>

      {/* 플로팅 버튼 (오른쪽 하단) */}
      <FloatingButtons />
    </div>
  );
}

export default CalendarPage;
