// /routes/CalendarPage.jsx
import React from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import "../styles/calendar.css"; // 기존 감성 스타일 CSS 연결
import FloatingButtons from "../components/FloatingButtons.jsx";

function CalendarPage() {
  return (
    <div className="calendar-page">
      {/* 상단 로고 & 월 표시 */}
      <header className="custom-header">
        <div className="logo">shedAI</div>
        <div className="month-title">
          <FullCalendar
            plugins={[dayGridPlugin]}
            initialView="dayGridMonth"
            headerToolbar={{
              start: "prev,next today",   // 왼쪽: 이전/다음/오늘 버튼
              center: "title",            // 가운데: 월/연도 표시
              end: "dayGridMonth,timeGridWeek,timeGridDay" // 오른쪽: 보기 전환
            }}
            events={[]}
             height="auto"
            dayHeaderContent={(args) => {
              const weekday = args.date.toLocaleDateString("en-US", { weekday: "short" });
              return <span>{weekday}</span>;
            }}
          />
        </div>
      </header>

      {/* 플로팅 버튼 (오른쪽 하단) */}
      <FloatingButtons />
    </div>
  );
}

export default CalendarPage;
