// /routes/CalendarPage.jsx
import { useRef } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import "../styles/calendar.css"; // 기존 감성 스타일 CSS 연결
import FloatingButtons from "../components/FloatingButtons.jsx";
import timeGridPlugin from "@fullcalendar/timegrid";


function CalendarPage() {
  return (
    <div className="calendar-page">
      {/* 커스텀 헤더 */}
      <header className="calendar-header">
        <div className="calendar-left">shedAI</div>

        <div className="calendar-center">
          <button className="nav-btn">‹</button>
          <span className="month-year">April 2025</span>
          <button className="nav-btn">›</button>
        </div>

        <div className="calendar-right">
          <button className="view-btn gray">today</button>
          <button className="view-btn">day</button>
          <button className="view-btn">week</button>
          <button className="view-btn active">month</button>
        </div>
      </header>

      {/* 캘린더 */}
      <div className="calendar-container">
        <FullCalendar
          plugins={[dayGridPlugin, timeGridPlugin]}
          initialView="dayGridMonth"
          headerToolbar={false}
          events={[]}
          height="auto"
          dayHeaderContent={(args) => {
            const weekday = args.date.toLocaleDateString("en-US", { weekday: "short" });
            return <span>{weekday}</span>;
          }}
        />
      </div>

      {/* 플로팅 버튼 (오른쪽 하단) */}
      <FloatingButtons />
    </div>
  );
}

export default CalendarPage;
